// src/components/LandingPage.js
import React, { useState, useEffect } from "react";
import { useNavigate, Link } from "react-router-dom";
import axios from "axios";
import "./LandingPage.css";

const api = axios.create({ baseURL: "http://localhost:5000/api" });

export default function LandingPage() {
  const navigate = useNavigate();

  /* ------- state ------- */
  const [symbols, setSymbols]       = useState([]);   // [{symbol, name}]
  const [searchTerm, setSearchTerm] = useState("");
  const [filtered, setFiltered]     = useState([]);
  const [quote, setQuote]           = useState(null);

  /* ------- tüm Nasdaq listesini ilk yüklemede çek ------- */
  useEffect(() => {
    api.get("/market/symbols")
       .then(res => setSymbols(res.data))
       .catch(err => console.error("Symbol list error:", err));
  }, []);

  /* ------- arama filtresi ------- */
  useEffect(() => {
    if (searchTerm.trim().length < 2) {
      setFiltered([]);
      return;
    }
    const t = searchTerm.trim().toUpperCase();
    setFiltered(
      symbols.filter(s => s.symbol.includes(t) || (s.name || "").toUpperCase().includes(t))
    );
  }, [searchTerm, symbols]);

  /* ------- quote getir ------- */
  const getQuote = (sym) => {
    api.get(`/market/quote/${sym}`)
       .then(res => setQuote(res.data))
       .catch(()  => setQuote(null));
  };

  /* ---------------- JSX ---------------- */
  return (
    <div className="landing-container">
      <header className="landing-header">
        <h2>Finansal Analiz</h2>
        <nav>
          <button onClick={() => navigate("/login")}>Giriş Yap</button>
          <button onClick={() => navigate("/register")}>Kayıt Ol</button>
        </nav>
      </header>

      <section className="hero-section">
        <div className="hero-content">
          <h1>Bitirme Projesi: Finansal Analiz</h1>
          <button onClick={() => navigate("/dashboard")}>Hemen Başla</button>
        </div>

        <aside className="markets-sidebar">
          <h3>Piyasalar (yfinance)</h3>
          <input
            type="text"
            placeholder="Hisse sembolü ara…"
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />

          <ul className="market-list">
            {filtered.length === 0 && searchTerm.trim().length >= 2 ? (
              <li>Sonuç bulunamadı.</li>
            ) : (
              filtered.map((s) => (
                <li key={s.symbol} onClick={() => getQuote(s.symbol)}>
                  {s.symbol} – {s.name}
                </li>
              ))
            )}
          </ul>

          {quote && (
            <div className="quote-result">
              <h4>{quote.symbol}</h4>
              <p>Fiyat: ${quote.price}</p>
              <p>
                Değişim: {quote.change} ({quote.percent}%)
              </p>
              <Link to={`/market/${quote.symbol}`}>Detay ➜</Link>
            </div>
          )}
        </aside>
      </section>
    </div>
  );
}
