import React, { useEffect } from 'react';
import './LandingPage.css';

const LandingPage = function() {
  // TradingView widget'ını sayfa yüklendiğinde eklemek için useEffect kullanıyoruz
  useEffect(function() {
    // tv.js dosyasını dinamik olarak ekle
    var script = document.createElement('script');
    script.type = 'text/javascript';
    script.src = 'https://s3.tradingview.com/tv.js';
    script.async = true;
    script.onload = function() {
      // TradingView'in widget fonksiyonunu kullanarak chart'ı yerleştir
      // Config ayarlarını TradingView dokümantasyonuna göre düzenleyebilirsiniz
      // Örnek: Market Overview widget, symbol listesi vb.
      new TradingView.widget({
        width: '100%',
        height: 400,
        symbol: 'BIST:XU100',         // BIST 100 endeksi örnek
        interval: '60',
        timezone: 'Etc/UTC',
        theme: 'dark',
        style: '1',
        locale: 'tr',
        toolbar_bg: '#0b0d17',
        hide_side_toolbar: false,
        allow_symbol_change: true,
        container_id: 'tradingview-chart'
      });
    };
    var container = document.getElementById('tradingview-widget');
    if (container) {
      container.appendChild(script);
    }
  }, []);

  // JSX yerine React.createElement kullanarak benzer TradingView arayüzünü oluşturalım
  return React.createElement(
    'div',
    { className: 'landing-container' },
    // ÜST NAVBAR
    React.createElement(
      'header',
      { className: 'tv-header' },
      // Sol kısım: Logo / Site adı
      React.createElement(
        'div',
        { className: 'tv-logo' },
        React.createElement('span', null, 'TradingClone')
      ),
      // Orta kısım: Menü
      React.createElement(
        'nav',
        { className: 'tv-nav' },
        React.createElement(
          'ul',
          null,
          React.createElement(
            'li',
            null,
            React.createElement('a', { href: '#urunler' }, 'Ürünler')
          ),
          React.createElement(
            'li',
            null,
            React.createElement('a', { href: '#topluluk' }, 'Topluluk')
          ),
          React.createElement(
            'li',
            null,
            React.createElement('a', { href: '#piyasalar' }, 'Piyasalar')
          ),
          React.createElement(
            'li',
            null,
            React.createElement('a', { href: '#aracikurum' }, 'Aracı Kurum')
          ),
          React.createElement(
            'li',
            null,
            React.createElement('a', { href: '#dahafazla' }, 'Daha Fazla')
          )
        )
      ),
      // Sağ kısım: Arama, Giriş, Yükselt
      React.createElement(
        'div',
        { className: 'tv-actions' },
        React.createElement(
          'div',
          { className: 'search-bar' },
          React.createElement('input', {
            type: 'text',
            placeholder: 'Ara (Ctrl+K)',
            className: 'search-input'
          })
        ),
        React.createElement(
          'button',
          { className: 'login-btn' },
          'Giriş'
        ),
        React.createElement(
          'button',
          { className: 'upgrade-btn' },
          'Yükselt'
        )
      )
    ),
    // ANA İÇERİK
    React.createElement(
      'main',
      { className: 'tv-main' },
      // Piyasa Özeti (üst kısım)
      React.createElement(
        'section',
        { className: 'market-summary' },
        React.createElement('h2', null, 'Piyasa Özeti'),
        React.createElement(
          'div',
          { className: 'index-list' },
          // Örnek endeksler
          React.createElement(
            'div',
            { className: 'index-item' },
            React.createElement('span', { className: 'index-name' }, 'BIST 100'),
            React.createElement('span', { className: 'index-change positive' }, '+1.77%')
          ),
          React.createElement(
            'div',
            { className: 'index-item' },
            React.createElement('span', { className: 'index-name' }, 'S&P 500'),
            React.createElement('span', { className: 'index-change negative' }, '-0.45%')
          ),
          React.createElement(
            'div',
            { className: 'index-item' },
            React.createElement('span', { className: 'index-name' }, 'Nasdaq 100'),
            React.createElement('span', { className: 'index-change negative' }, '-0.32%')
          ),
          React.createElement(
            'div',
            { className: 'index-item' },
            React.createElement('span', { className: 'index-name' }, 'Dow 30'),
            React.createElement('span', { className: 'index-change positive' }, '+0.21%')
          )
        )
      ),
      // TradingView Chart bölümü
      React.createElement(
        'section',
        { className: 'chart-section' },
        React.createElement(
          'div',
          { id: 'tradingview-widget', className: 'tradingview-widget-container' },
          // Bu div içine widget yerleşecek
          React.createElement('div', { id: 'tradingview-chart', className: 'tradingview-chart' })
        )
      )
    )
  );
};

export default LandingPage;
