import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const API_BASE = 'http://localhost:5000';  // Flask backend adresi

function MarketDetail() {
  const { symbol } = useParams();
  const [detailData, setDetailData] = useState(null);
  const [quote, setQuote] = useState(null);
  const [prediction, setPrediction] = useState(null);
  const [loadingPrediction, setLoadingPrediction] = useState(true);
  const [predictionError, setPredictionError] = useState(false);

  const FINNHUB_API_KEY = 'cva1t91r01qpd9sa8bi0cva1t91r01qpd9sa8big';

  // Şirket profilini çek
  useEffect(() => {
    axios
      .get(`https://finnhub.io/api/v1/stock/profile2?symbol=${symbol}&token=${FINNHUB_API_KEY}`)
      .then(res => setDetailData(res.data))
      .catch(err => console.error('Profil verisi alınırken hata:', err));
  }, [symbol]);

  // Güncel fiyat verilerini çek
  useEffect(() => {
    axios
      .get(`https://finnhub.io/api/v1/quote?symbol=${symbol}&token=${FINNHUB_API_KEY}`)
      .then(res => setQuote(res.data))
      .catch(err => console.error('Fiyat verisi alınırken hata:', err));
  }, [symbol]);

  // Gerçek model tahminini çek
  useEffect(() => {
    setLoadingPrediction(true);
    setPredictionError(false);
    axios
      .get(`${API_BASE}/api/predict/${symbol}`)
      .then(res => {
        setPrediction(res.data);
      })
      .catch(err => {
        console.error('Tahmin verisi alınırken hata:', err);
        setPredictionError(true);
      })
      .finally(() => {
        setLoadingPrediction(false);
      });
  }, [symbol]);

  // Veri yükleniyorsa
  if (!detailData || !quote) {
    return <div>Yükleniyor...</div>;
  }

  return (
    <div style={{ padding: 20, color: '#fff' }}>
      <h2>{detailData.name} Detay Sayfası</h2>
      <p><strong>Sembol:</strong> {detailData.ticker}</p>
      <p><strong>Ülke:</strong> {detailData.country}</p>
      <p><strong>Borsa:</strong> {detailData.exchange}</p>
      <p><strong>Güncel Fiyat:</strong> ${quote.c}</p>
      <p><strong>Değişim:</strong> {quote.d.toFixed(2)} ({quote.dp.toFixed(2)}%)</p>

      {/* Model Tahmini Bölümü */}
      {loadingPrediction && <p>Tahmin yükleniyor...</p>}
      {predictionError && (
        <p style={{ color: 'red' }}>Tahmin alınırken bir hata oluştu.</p>
      )}
      {prediction && !predictionError && !loadingPrediction && (
        <div style={{ marginTop: 20, padding: 15, backgroundColor: '#1e293b', borderRadius: 8 }}>
          <h3 style={{ marginBottom: 10 }}>Model Tahmini</h3>
          <p><strong>Fiyat:</strong> ${prediction.predictedPrice.toFixed(2)}</p>
          <p><strong>Yön:</strong> {prediction.direction === 'up' ? 'YÜKSELİŞ' : 'DÜŞÜŞ'}</p>
          <p><strong>Sentiment:</strong> {prediction.sentiment.toFixed(4)}</p>
        </div>
      )}
    </div>
  );
}

export default MarketDetail;
