// src/components/Dashboard.js
import React, { useEffect, useState } from "react";
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer } from "recharts";
import "./Dashboard.css";

export default function Dashboard() {
  // Demo verileri; bunları backend’den çektiğin income/expense API ile değiştir
  const [totals, setTotals] = useState({ income: 10000, expense: 7500 });
  const [history, setHistory] = useState([
    { name: "Oca", Gelir: 2000, Gider: 1500 },
    { name: "Şub", Gelir: 1800, Gider: 1700 },
    { name: "Mar", Gelir: 2200, Gider: 1300 },
    { name: "Nis", Gelir: 1500, Gider: 1200 },
    { name: "May", Gelir: 2500, Gider: 1900 },
  ]);

  // İleride gerçek veriyi şu şekilde çekebilirsin:
  // useEffect(() => {
  //   axios.get("/api/transactions/summary").then(r => setTotals(r.data));
  //   axios.get("/api/transactions/history").then(r => setHistory(r.data));
  // }, []);

  return (
    <div className="dashboard-wrapper">
      {/* Üst istatistik kartları */}
      <div className="stats-row">
        <div className="stat-card">
          <h3>Toplam Gelir</h3>
          <p>₺{totals.income.toLocaleString()}</p>
        </div>
        <div className="stat-card">
          <h3>Toplam Gider</h3>
          <p>₺{totals.expense.toLocaleString()}</p>
        </div>
        <div className="stat-card">
          <h3>Kalan Bakiye</h3>
          <p>₺{(totals.income - totals.expense).toLocaleString()}</p>
        </div>
      </div>

      {/* Alt grafikler */}
      <div className="content-row">
        {/* Gelir/Gider Grafiği */}
        <div className="chart-card">
          <h4>Gelir / Gider Grafiği</h4>
          <ResponsiveContainer width="100%" height={250}>
            <BarChart data={history} margin={{ top: 0, right: 20, left: 0, bottom: 0 }}>
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis dataKey="name" />
              <YAxis />
              <Tooltip />
              <Bar dataKey="Gelir" fill="#22c55e" />
              <Bar dataKey="Gider" fill="#ef4444" />
            </BarChart>
          </ResponsiveContainer>
        </div>

        {/* Yatırım Önerileri Kartı */}
        <div className="chart-card">
          <h4>Yatırım Önerileri</h4>
          <ul style={{ paddingLeft: "1rem", margin: 0, color: "#334155" }}>
            <li>Apple (AAPL): AL</li>
            <li>Microsoft (MSFT): TUT</li>
            <li>Tesla (TSLA): SAT</li>
          </ul>
        </div>
      </div>
    </div>
);
}
