import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

function MarketDetail() {
  const { symbol } = useParams();
  const [detailData, setDetailData] = useState(null);
  const [quote, setQuote] = useState(null);
  const FINNHUB_API_KEY = 'YOUR_API_KEY';

  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const url = `https://finnhub.io/api/v1/stock/profile2?symbol=${symbol}&token=${FINNHUB_API_KEY}`;
        const res = await axios.get(url);
        console.log('Profile response:', res.data);
        setDetailData(res.data);
      } catch (error) {
        console.error('Profil verisi alınırken hata:', error);
      }
    };
    fetchProfile();
  }, [symbol]);

  useEffect(() => {
    const fetchQuote = async () => {
      try {
        const quoteUrl = `https://finnhub.io/api/v1/quote?symbol=${symbol}&token=${FINNHUB_API_KEY}`;
        const quoteRes = await axios.get(quoteUrl);
        console.log('Quote response:', quoteRes.data);
        setQuote(quoteRes.data);
      } catch (error) {
        console.error('Fiyat verisi alınırken hata:', error);
      }
    };
    fetchQuote();
  }, [symbol]);

  if (!detailData || !quote) {
    return <div>Yükleniyor...</div>;
  }

  // Eğer data boşsa, Finnhub “{}” döndürebilir.
  // “s” alanı “ok” değilse, veriler gelmemiş olabilir.
  // Dilerseniz if (Object.keys(detailData).length === 0) { ... } ekleyebilirsiniz.

  return (
    <div>
      <h2>{detailData.name} Detay Sayfası</h2>
      <p>Sembol: {detailData.ticker}</p>
      <p>Ülke: {detailData.country}</p>
      <p>Borsa: {detailData.exchange}</p>
      <p>Güncel Fiyat: ${quote.c}</p>
      <p>Değişim: {quote.d?.toFixed(2)} ({quote.dp?.toFixed(2)}%)</p>
    </div>
  );
}

export default MarketDetail;
