import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './LandingPage.css';

function LandingPage() {
  // Dinamik arama için state
  const [searchTerm, setSearchTerm] = useState('');
  const [searchResults, setSearchResults] = useState([]);

  // Alpha Vantage API Key (Kendi API anahtarınızı buraya yazın)
  const ALPHAVANTAGE_API_KEY = 'R9QMG5MRESRAFFYX';

  // Arama terimine göre dinamik sembol araması (debounce ile)
  useEffect(() => {
    const fetchSearchResults = async () => {
      if (searchTerm.trim().length < 2) {
        setSearchResults([]);
        return;
      }
      try {
        const url = `https://www.alphavantage.co/query?function=SYMBOL_SEARCH&keywords=${encodeURIComponent(
          searchTerm
        )}&apikey=${ALPHAVANTAGE_API_KEY}`;
        const response = await axios.get(url);
        // API yanıtında "bestMatches" dizisini alıyoruz
        const results = response.data.bestMatches || [];
        setSearchResults(results);
      } catch (err) {
        console.error('Arama API Hatası:', err);
        setSearchResults([]);
      }
    };

    const delayDebounceFn = setTimeout(() => {
      fetchSearchResults();
    }, 500);

    return () => clearTimeout(delayDebounceFn);
  }, [searchTerm, ALPHAVANTAGE_API_KEY]);

  return (
    <div className="landing-container">
      {/* Üst Kısım (Header / Navbar) */}
      <header className="landing-header">
        <div className="logo-area">
          <h2 className="logo-text">Finansal Analiz</h2>
        </div>
        <nav className="navbar">
          <ul>
            <li><a href="#features">Özellikler</a></li>
            <li><a href="#technology">Teknolojiler</a></li>
            <li><a href="#about">Hakkımızda</a></li>
            <li><a href="#contact">İletişim</a></li>
            <li><a href="/Login">Giriş Yap</a></li>
            <li><a href="/Register">Kayıt Ol</a></li>
          </ul>
        </nav>
      </header>

      {/* Hero Bölümü */}
      <section className="hero-section">
        {/* Sol Kısım: Başlık, Açıklama, Buton */}
        <div className="hero-content">
          <h1 className="hero-title">
            Bitirme Projesi: Finansal Analiz Uygulaması
          </h1>
          <p className="hero-subtitle">
            Gelir-gider takibi, bütçe yönetimi ve yatırım önerileri tek platformda.
            Makine öğrenmesi destekli analizlerle daha bilinçli finansal kararlar alın!
          </p>
          <button className="cta-button">Hemen Başla</button>
        </div>

        {/* Sağ Kısım: Piyasalar Bölümü (Dinamik Arama) */}
        <aside className="markets-sidebar">
          <h3>Piyasalar</h3>
          {/* Arama Kutusu */}
          <div className="search-bar">
            <input
              type="text"
              placeholder="Hisse veya Kripto Ara..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
            />
          </div>
          {/* Arama Sonuçlarını Listele */}
          <ul className="market-list">
            {searchResults.length === 0 && searchTerm.trim().length >= 2 ? (
              <li className="market-item">Sonuç bulunamadı.</li>
            ) : (
              searchResults.map((item, index) => (
                <li key={index} className="market-item">
                  <span className="market-name">
                    {item['1. symbol']} - {item['2. name']}
                  </span>
                </li>
              ))
            )}
          </ul>
        </aside>
      </section>

      {/* Özellikler Bölümü */}
      <section id="features" className="features-section">
        <h2>Öne Çıkan Özellikler</h2>
        <div className="features-container">
          <div className="feature-card">
            <h3>Finansal Durum Görselleştirme</h3>
            <p>
              Gelir ve giderlerinizi grafikler ve tablolarla takip edin.
              Harcamalarınızı kategorize ederek bütçenizi daha iyi yönetin.
            </p>
          </div>
          <div className="feature-card">
            <h3>Gelir-Gider Takibi</h3>
            <p>
              Günlük/aylık giderlerinizi kaydedin, bütçe hedefleri belirleyin
              ve geçmiş harcama trendlerinizi inceleyin.
            </p>
          </div>
          <div className="feature-card">
            <h3>Yatırım Önerileri</h3>
            <p>
              Makine öğrenmesi modelleriyle potansiyel yatırım fırsatlarını
              analiz ederek size özel öneriler sunar.
            </p>
