import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

function MarketDetail() {
  const { symbol } = useParams();
  const [detailData, setDetailData] = useState(null);
  const [quote, setQuote] = useState(null);
  const FINNHUB_API_KEY = 'cva1t91r01qpd9sa8bi0cva1t91r01qpd9sa8big';

  // Şirket profilini çekelim
  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const profileUrl = `https://finnhub.io/api/v1/stock/profile2?symbol=${symbol}&token=${FINNHUB_API_KEY}`;
        const profileResponse = await axios.get(profileUrl);
        setDetailData(profileResponse.data);
      } catch (error) {
        console.error('Profil verisi alınırken hata:', error);
      }
    };
    fetchProfile();
  }, [symbol]);

  // Fiyat verilerini çekelim
  useEffect(() => {
    const fetchQuote = async () => {
      try {
        const quoteUrl = `https://finnhub.io/api/v1/quote?symbol=${symbol}&token=${FINNHUB_API_KEY}`;
        const quoteResponse = await axios.get(quoteUrl);
        setQuote(quoteResponse.data);
      } catch (error) {
        console.error('Fiyat verisi alınırken hata:', error);
      }
    };
    fetchQuote();
  }, [symbol]);

  if (!detailData || !quote) {
    return <div>Yükleniyor...</div>;
  }

  return (
    <div>
      <h2>{detailData.name} Detay Sayfası</h2>
      <p>Sembol: {detailData.ticker}</p>
      <p>Ülke: {detailData.country}</p>
      <p>Borsa: {detailData.exchange}</p>
      <p>Güncel Fiyat: ${quote.c}</p>
      <p>Değişim: {quote.d} ({quote.dp}%)</p>
      {/* İstediğiniz diğer bilgileri de ekleyebilirsiniz */}
    </div>
  );
}

export default MarketDetail;
