// src/components/Dashboard.js
import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import {
  BarChart,
  Bar,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  ResponsiveContainer,
} from "recharts";

import TransactionForm from "./TransactionForm";
import "./Dashboard.css";

export default function Dashboard() {
  const navigate = useNavigate();
  const [totals, setTotals] = useState({ income: 0, expense: 0 });
  const [history, setHistory] = useState([]);

  const fetchData = () => {
    const token = localStorage.getItem("token");
    axios
      .get("http://localhost:5000/api/transactions/summary", {
        headers: { Authorization: `Bearer ${token}` },
      })
      .then((res) => setTotals(res.data))
      .catch((e) => console.error("Summary hata:", e));

    axios
      .get("http://localhost:5000/api/transactions/history", {
        headers: { Authorization: `Bearer ${token}` },
      })
      .then((res) => setHistory(res.data))
      .catch((e) => console.error("History hata:", e));
  };

  useEffect(() => {
    fetchData();
  }, []);

  const handleLogout = () => {
    localStorage.removeItem("token");
    navigate("/login");
  };

  return (
    <div className="dashboard-wrapper">
      {/* 1) Yeni işlem formu */}
      <TransactionForm onSuccess={fetchData} />

      {/* 2) Çıkış Yap */}
      <div className="logout-container">
        <button className="logout-button" onClick={handleLogout}>
          Çıkış Yap
        </button>
      </div>

      {/* 3) Üst İstatistik Kartları */}
      <div className="stats-row">
        <div className="stat-card">
          <h3>Toplam Gelir</h3>
          <p>₺{totals.income.toLocaleString()}</p>
        </div>
        <div className="stat-card">
          <h3>Toplam Gider</h3>
          <p>₺{totals.expense.toLocaleString()}</p>
        </div>
        <div className="stat-card">
          <h3>Kalan Bakiye</h3>
          <p>₺{(totals.income - totals.expense).toLocaleString()}</p>
        </div>
      </div>

      {/* 4) Alt Grafikler ve Öneriler */}
      <div className="content-row">
        <div className="chart-card">
          <h4>Gelir / Gider Grafiği</h4>
          <ResponsiveContainer width="100%" height={250}>
            <BarChart
              data={history}
              margin={{ top: 0, right: 20, left: 0, bottom: 0 }}
            >
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis dataKey="name" />
              <YAxis />
              <Tooltip />
              <Bar dataKey="Gelir" fill="#22c55e" />
              <Bar dataKey="Gider" fill="#ef4444" />
            </BarChart>
          </ResponsiveContainer>
        </div>
        <div className="chart-card">
          <h4>Yatırım Önerileri</h4>
          <ul style={{ paddingLeft: "1rem", margin: 0, color: "#334155" }}>
            <li>Apple (AAPL): AL</li>
            <li>Microsoft (MSFT): TUT</li>
            <li>Tesla (TSLA): SAT</li>
          </ul>
        </div>
      </div>
    </div>
  );
}
