// src/components/LandingPage.js
import React, { useState, useEffect } from "react";
import { useNavigate, Link } from "react-router-dom";
import axios from "axios";
import "./LandingPage.css";

const FINNHUB_KEY = "cva1t91r01qpd9sa8bi0cva1t91r01qpd9sa8big";

/* Finnhub için ortak axios instance – TOKEN’i header’da taşıyoruz */
const finnhub = axios.create({
  baseURL: "https://finnhub.io/api/v1",
  headers: { "X-Finnhub-Token": FINNHUB_KEY },
});

function LandingPage() {
  const navigate = useNavigate();

  const [searchTerm, setSearchTerm] = useState("");
  const [searchResults, setSearchResults] = useState([]);

  const [nasdaqSymbols, setNasdaqSymbols] = useState([]);

  /* --- Yardımcı: slash ekle --- */
  const insertSlash = (t) => {
    const s = t.toUpperCase().trim();
    if (s.length === 6 || s.length === 7) return s.slice(0, 3) + "/" + s.slice(3);
    return s;
  };

  /* Finnhub search – header’lı */
  const fetchFinnhubSymbols = async (term) => {
    const { data } = await finnhub.get("/search", {
      params: { q: term },
    });
    return data.result || [];
  };

  /* Nasdaq sembol listesini çek */
  useEffect(() => {
    const fetchNasdaqSymbols = async () => {
      try {
        const { data } = await finnhub.get("/stock/symbol", {
          params: { exchange: "US" },
        });
        const list = data.filter((x) => x.mic === "XNAS").map((x) => x.symbol);
        setNasdaqSymbols(list);
      } catch (err) {
        console.error("Nasdaq sembolleri alınamadı:", err);
      }
    };
    fetchNasdaqSymbols();
  }, []);

  /* Arama */
  useEffect(() => {
    if (searchTerm.trim().length < 2) {
      setSearchResults([]);
      return;
    }

    const doSearch = async () => {
      try {
        const term = searchTerm.trim().toUpperCase();

        const direct = await fetchFinnhubSymbols(term);
        const slashT = insertSlash(term);
        const slash = slashT !== term ? await fetchFinnhubSymbols(slashT) : [];

        const combined = [...direct, ...slash];
        const uniq = [];
        const seen = new Set();
        for (const it of combined) {
          if (!seen.has(it.symbol)) {
            seen.add(it.symbol);
            uniq.push(it);
          }
        }

        const filtered = uniq.filter((x) =>
          x.symbol.toUpperCase().includes(term)
        );

        const onlyNasdaq =
          nasdaqSymbols.length > 0
            ? filtered.filter((x) => nasdaqSymbols.includes(x.symbol))
            : filtered;

        setSearchResults(onlyNasdaq);
      } catch (e) {
        console.error("Finnhub API Hatası:", e);
        setSearchResults([]);
      }
    };

    const tid = setTimeout(doSearch, 500);
    return () => clearTimeout(tid);
  }, [searchTerm, nasdaqSymbols]);

  return (
    <div className="landing-container">
      {/* HEADER */}
      <header className="landing-header">
        <div className="logo-area">
          <h2 className="logo-text">Finansal Analiz</h2>
        </div>
        <nav className="navbar">
          <ul>
            <li><a href="#features">Özellikler</a></li>
            <li><a href="#technology">Teknolojiler</a></li>
            <li><a href="#about">Hakkımızda</a></li>
            <li><a href="#contact">İletişim</a></li>
            <li><a href="/Login">Giriş Yap</a></li>
            <li><a href="/Register">Kayıt Ol</a></li>
          </ul>
        </nav>
      </header>

      {/* HERO */}
      <section className="hero-section">
        <div className="hero-content">
          <h1 className="hero-title">Bitirme Projesi: Finansal Analiz</h1>
          <p className="hero-subtitle">
            Gelir–gider takibi, bütçe yönetimi ve yatırım önerileri tek
            platformda. Makine öğrenmesi destekli analizlerle daha bilinçli
            finansal kararlar alın!
          </p>
          <button className="cta-button" onClick={() => navigate("/register")}>
            Hemen Başla
          </button>
        </div>

        {/* Piyasalar / Arama */}
        <aside className="markets-sidebar">
          <h3>Piyasalar (Finnhub)</h3>
          <div className="search-bar">
            <input
              type="text"
              placeholder="Hisse Ara…"
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
            />
          </div>

          <ul className="market-list">
            {searchResults.length === 0 && searchTerm.trim().length >= 2 ? (
              <li className="market-item">Sonuç bulunamadı.</li>
            ) : (
              searchResults.map((it) => (
                <li key={it.symbol} className="market-item">
                  <Link to={`/market/${it.symbol}`}>
                    {it.symbol} – {it.description}
                  </Link>
                </li>
              ))
            )}
          </ul>
        </aside>
      </section>

      {/* … devamı aynı … */}
    </div>
  );
}

export default LandingPage;
