// src/components/LandingPage.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate, Link } from 'react-router-dom';
import './LandingPage.css';

// Finnhub token ve axios instance
const FINNHUB_KEY = 'cva1t91r01qpd9sa8bi0cva1t91r01qpd9sa8big';
const finnhub = axios.create({
  baseURL: 'https://finnhub.io/api/v1',
  headers: { 'X-Finnhub-Token': FINNHUB_KEY },
});

function LandingPage() {
  const [searchTerm, setSearchTerm] = useState('');
  const [searchResults, setSearchResults] = useState([]);
  const [nasdaqSymbols, setNasdaqSymbols] = useState([]);
  const navigate = useNavigate();

  // Slash varyasyonu (örn: ABCDEF -> ABC/DEF)
  const insertSlash = term => {
    const t = term.trim().toUpperCase();
    return (t.length === 6 || t.length === 7) ? `${t.slice(0,3)}/${t.slice(3)}` : t;
  };

  // Nasdaq sembollerini bir kez çek
  useEffect(() => {
    const fetchNasdaq = async () => {
      try {
        const { data } = await finnhub.get('/stock/symbol', { params: { exchange: 'US' } });
        const list = data.filter(item => item.mic === 'XNAS').map(item => item.symbol);
        setNasdaqSymbols(list);
      } catch (err) {
        console.error('Nasdaq sembolleri alınamadı:', err);
      }
    };
    fetchNasdaq();
  }, []);

  // Arama (debounce)
  useEffect(() => {
    if (searchTerm.trim().length < 2) {
      setSearchResults([]);
      return;
    }
    const timer = setTimeout(async () => {
      try {
        const term = searchTerm.trim().toUpperCase();
        // Doğrudan arama
        const direct = (await finnhub.get('/search', { params: { q: term } })).data.result || [];
        // Slash varyasyonu
        const slashTerm = insertSlash(term);
        const slash = slashTerm !== term
          ? (await finnhub.get('/search', { params: { q: slashTerm } })).data.result || []
          : [];
        // Benzersizleştir ve Nasdaq filtresi
        const map = new Map();
        [...direct, ...slash].forEach(item => map.set(item.symbol, item));
        const filtered = [...map.values()].filter(item => nasdaqSymbols.includes(item.symbol));
        setSearchResults(filtered);
      } catch (err) {
        console.error('Arama hatası:', err);
        setSearchResults([]);
      }
    }, 500);
    return () => clearTimeout(timer);
  }, [searchTerm, nasdaqSymbols]);

  return (
    <div className="landing-container">
      {/* HEADER */}
      <header className="landing-header">
        <div className="logo-area">
          <h2 className="logo-text">Finansal Analiz</h2>
        </div>
        <nav className="navbar">
          <ul>
            <li><a href="#features">Özellikler</a></li>
            <li><a href="#technology">Teknolojiler</a></li>
            <li><a href="#about">Hakkımızda</a></li>
            <li><a href="#contact">İletişim</a></li>
            <li><a href="/login">Giriş Yap</a></li>
            <li><a href="/register">Kayıt Ol</a></li>
          </ul>
        </nav>
      </header>

      {/* HERO + PİYASALAR */}
      <section className="hero-section">
        <div className="hero-content">
          <h1 className="hero-title">Bitirme Projesi: Finansal Analiz Uygulaması</h1>
          <p className="hero-subtitle">
            Gelir-gider takibi, bütçe yönetimi ve yatırım önerileri tek platformda.
            Makine öğrenmesi destekli analizlerle daha bilinçli finansal kararlar alın!
          </p>
          <button className="cta-button" onClick={() => navigate('/register')}>Hemen Başla</button>
        </div>

        <aside className="markets-sidebar">
          <h3>Piyasalar (Finnhub)</h3>
          <div className="search-bar">
            <input
              type="text"
              placeholder="Hisse sembolü ara…"
              value={searchTerm}
              onChange={e => setSearchTerm(e.target.value)}
            />
          </div>
          <ul className="market-list">
            {searchResults.length === 0 && searchTerm.trim().length >= 2 ? (
              <li className="market-item">Sonuç bulunamadı.</li>
            ) : (
              searchResults.map(item => (
                <li key={item.symbol} className="market-item">
                  <Link to={`/market/${item.symbol}`}>{item.symbol} – {item.description}</Link>
                </li>
              ))
            )}
          </ul>
        </aside>
      </section>

      {/* Diğer bölümler (features, technologies, about, footer) burada kalır */}
    </div>
  );
}

export default LandingPage;
