// src/components/LandingPage.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate, Link } from 'react-router-dom';
import './LandingPage.css';

const FINNHUB_KEY = 'cva1t91r01qpd9sa8bi0cva1t91r01qpd9sa8big';
const finnhub = axios.create({
  baseURL: '/finnhub',
  headers: { 'X-Finnhub-Token': FINNHUB_KEY }
});

export default function LandingPage() {
  const [searchTerm, setSearchTerm]       = useState('');
  const [searchResults, setSearchResults] = useState([]);
  const [nasdaqSymbols, setNasdaqSymbols] = useState([]);
  const navigate = useNavigate();

  /**  Sembol “ABCDEF” ⇒ “ABC/DEF”  */
  const insertSlash = t =>
    (t.length === 6 || t.length === 7)
      ? `${t.slice(0, 3)}/${t.slice(3)}`
      : t;

  /* Nasdaq listesi (bir kere) */
  useEffect(() => {
    (async () => {
      try {
        const { data } = await finnhub.get('/stock/symbol', { params: { exchange: 'US' } });
        setNasdaqSymbols(data.filter(x => x.mic === 'XNAS').map(x => x.symbol));
      } catch (e) { console.error('Nasdaq sembolleri alınamadı:', e); }
    })();
  }, []);

  /* Arama (debounce) */
  useEffect(() => {
    if (searchTerm.trim().length < 2) return setSearchResults([]);
    const handle = setTimeout(async () => {
      try {
        const term = searchTerm.trim().toUpperCase();
        const direct = (await finnhub.get('/search', { params: { q: term } })).data.result || [];
        const slash  = term !== insertSlash(term)
          ? (await finnhub.get('/search', { params: { q: insertSlash(term) } })).data.result || []
          : [];
        const uniq = new Map([...direct, ...slash].map(r => [r.symbol, r]));
        setSearchResults([...uniq.values()].filter(r => nasdaqSymbols.includes(r.symbol)));
      } catch (e) { console.error('Arama hatası:', e); }
    }, 500);
    return () => clearTimeout(handle);
  }, [searchTerm, nasdaqSymbols]);

  /* ---------- JSX ---------- */
  return (
    <div className="landing-container">
      {/* == HEADER ================================================= */}
      <header className="landing-header">
        <h2 className="logo-text">Finansal Analiz</h2>
        <nav className="navbar">
          <ul>
            <li><a href="#features">Özellikler</a></li>
            <li><a href="#technology">Teknolojiler</a></li>
            <li><a href="#about">Hakkımızda</a></li>
            <li><a href="#contact">İletişim</a></li>
            <li><Link to="/login">Giriş Yap</Link></li>
            <li><Link to="/register">Kayıt Ol</Link></li>
          </ul>
        </nav>
      </header>

      {/* == HERO + PİYASA SEARCH ================================= */}
      <section className="hero-section">
        <div className="hero-content">
          <h1 className="hero-title">Bitirme Projesi: Finansal Analiz Uygulaması</h1>
          <p className="hero-subtitle">
            Gelir-gider takibi, bütçe yönetimi ve yatırım önerileri tek platformda.
            Makine öğrenmesi destekli analizlerle daha bilinçli finansal kararlar alın!
          </p>
          <button className="cta-button" onClick={() => navigate('/register')}>Hemen Başla</button>
        </div>

        <aside className="markets-sidebar">
          <h3>Piyasalar (Finnhub)</h3>
          <input
            className="market-search"
            placeholder="Hisse sembolü ara…"
            value={searchTerm}
            onChange={e => setSearchTerm(e.target.value)}
          />
          <ul className="market-list">
            {searchResults.length === 0 && searchTerm.trim().length >= 2
              ? <li className="market-item">Sonuç bulunamadı.</li>
              : searchResults.map(r => (
                  <li key={r.symbol} className="market-item">
                    <Link to={`/market/${r.symbol}`}>{r.symbol} – {r.description}</Link>
                  </li>
                ))}
          </ul>
        </aside>
      </section>

      {/* == ÖZELLİKLER =========================================== */}
      <section id="features" className="features-section">
        <h2>Özellikler</h2>
        <div className="features-container">
          <div className="feature-card">
            <h3>Gelir–Gider Takibi</h3>
            <p>Akıllı grafiklerle günlük, aylık ve yıllık nakit akışını anlık izle.</p>
          </div>
          <div className="feature-card">
            <h3>Bütçe Yönetimi</h3>
            <p>Kişisel harcama kategorileri tanımla, limit aşımı uyarıları al.</p>
          </div>
          <div className="feature-card">
            <h3>Yatırım Önerileri</h3>
            <p>Makine öğrenmesi ile portföyüne uygun hisse ve fon önerileri.</p>
          </div>
          <div className="feature-card">
            <h3>Gerçek-Zamanlı Veriler</h3>
            <p>Finnhub entegrasyonu ile anlık piyasa verileri ve haber akışı.</p>
          </div>
        </div>
      </section>

      {/* == TEKNOLOJİLER ========================================= */}
      <section id="technology" className="technology-section">
        <h2>Kullanılan Teknolojiler</h2>
        <div className="tech-grid">
          <div className="tech-item">
            <img src="/logos/react.svg" alt="React" />
            <span>React</span>
          </div>
          <div className="tech-item">
            <img src="/logos/flask.svg" alt="Flask" />
            <span>Flask</span>
          </div>
          <div className="tech-item">
            <img src="/logos/postgresql.svg" alt="PostgreSQL" />
            <span>PostgreSQL</span>
          </div>
          <div className="tech-item">
            <img src="/logos/sklearn.svg" alt="Scikit-Learn" />
            <span>Sk-learn</span>
          </div>
        </div>
      </section>

      {/* == HAKKIMIZDA =========================================== */}
      <section id="about" className="about-section">
        <h2>Hakkımızda</h2>
        <p>
          Bu proje, Bandırma Onyedi Eylül Üniversitesi Bilgisayar Mühendisliği
          öğrencileri <strong>Hasancan Eser</strong> ve <strong>Davut Kırtak</strong>
          tarafından, danışman <strong>Doç. Dr. Mehmet Akif Çiftçi</strong>
          rehberliğinde geliştirilmektedir. Amacımız, kişisel finans yönetimini
          basitleştirerek kullanıcıların bilinçli yatırım kararı almasını sağlamaktır.
        </p>
      </section>

      {/* == İLETİŞİM ============================================ */}
      <section id="contact" className="contact-section">
        <h2>İletişim</h2>
        <form
          className="contact-form"
          onSubmit={e => {
            e.preventDefault();
            alert('Mesajınız alınmıştır! 🎉');
            e.target.reset();
          }}
        >
          <input type="text" placeholder="Adınız" required />
          <input type="email" placeholder="E-posta" required />
          <textarea rows="4" placeholder="Mesajınız…" required />
          <button type="submit">Gönder</button>
        </form>
      </section>

      {/* == FOOTER ============================================== */}
      <footer className="landing-footer">
        <p>© {new Date().getFullYear()} Finansal Analiz • Tüm hakları saklıdır.</p>
        <p style={{ fontSize: '.8rem' }}>Bu sayfa tamamen bitirme projesi amaçlı hazırlanmıştır.</p>
      </footer>
    </div>
  );
}
