import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

function MarketDetail() {
  const { symbol } = useParams();
  const [detailData, setDetailData] = useState(null);
  const [quote, setQuote] = useState(null);
  const [prediction, setPrediction] = useState(null);

  const FINNHUB_API_KEY = 'cva1t91r01qpd9sa8bi0cva1t91r01qpd9sa8big';

  // Şirket profilini çekelim
  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const profileUrl = `https://finnhub.io/api/v1/stock/profile2?symbol=${symbol}&token=${FINNHUB_API_KEY}`;
        const profileResponse = await axios.get(profileUrl);
        setDetailData(profileResponse.data);
      } catch (error) {
        console.error('Profil verisi alınırken hata:', error);
      }
    };
    fetchProfile();
  }, [symbol]);

  // Fiyat verilerini çekelim
  useEffect(() => {
    const fetchQuote = async () => {
      try {
        const quoteUrl = `https://finnhub.io/api/v1/quote?symbol=${symbol}&token=${FINNHUB_API_KEY}`;
        const quoteResponse = await axios.get(quoteUrl);
        setQuote(quoteResponse.data);
      } catch (error) {
        console.error('Fiyat verisi alınırken hata:', error);
      }
    };
    fetchQuote();
  }, [symbol]);

  // Model tahminini çekelim
  useEffect(() => {
    const fetchPrediction = async () => {
      try {
        const res = await axios.get(`/api/predict/${symbol}`);
        setPrediction(res.data);
      } catch (error) {
        console.error('Tahmin verisi alınırken hata:', error);
      }
    };
    fetchPrediction();
  }, [symbol]);

  if (!detailData || !quote) {
    return <div>Yükleniyor...</div>;
  }

  return (
    <div style={{ padding: 20, color: '#fff' }}>
      <h2>{detailData.name} Detay Sayfası</h2>
      <p><strong>Sembol:</strong> {detailData.ticker}</p>
      <p><strong>Ülke:</strong> {detailData.country}</p>
      <p><strong>Borsa:</strong> {detailData.exchange}</p>
      <p><strong>Güncel Fiyat:</strong> ${quote.c}</p>
      <p><strong>Değişim:</strong> {quote.d.toFixed(2)} ({quote.dp.toFixed(2)}%)</p>

      {prediction && (
        <div style={{
          marginTop: 20,
          padding: 15,
          backgroundColor: '#1e293b',
          borderRadius: 8
        }}>
          <h3 style={{ marginBottom: 10 }}>Model Tahmini</h3>
          <p><strong>Fiyat:</strong> ${prediction.predictedPrice.toFixed(2)}</p>
          <p><strong>Yön:</strong> {prediction.direction === 'up' ? 'YÜKSELİŞ' : 'DÜŞÜŞ'}</p>
          <p><strong>Sentiment:</strong> {prediction.sentiment.toFixed(4)}</p>
        </div>
      )}
    </div>
  );
}

export default MarketDetail;
