import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

function MarketDetail() {
  const { symbol } = useParams();
  const [detailData, setDetailData] = useState(null);
  const [quote, setQuote] = useState(null);
  const [news, setNews] = useState([]);
  const FINNHUB_API_KEY = 'cva1t91r01qpd9sa8bi0cva1t91r01qpd9sa8big';

  // Şirket profilini çekelim
  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const profileUrl = `https://finnhub.io/api/v1/stock/profile2?symbol=${symbol}&token=${FINNHUB_API_KEY}`;
        const profileResponse = await axios.get(profileUrl);
        setDetailData(profileResponse.data);
      } catch (error) {
        console.error('Profil verisi alınırken hata:', error);
      }
    };
    fetchProfile();
  }, [symbol, FINNHUB_API_KEY]);

  // Güncel fiyat verilerini çekelim
  useEffect(() => {
    const fetchQuote = async () => {
      try {
        const quoteUrl = `https://finnhub.io/api/v1/quote?symbol=${symbol}&token=${FINNHUB_API_KEY}`;
        const quoteResponse = await axios.get(quoteUrl);
        setQuote(quoteResponse.data);
      } catch (error) {
        console.error('Fiyat verisi alınırken hata:', error);
      }
    };
    fetchQuote();
  }, [symbol, FINNHUB_API_KEY]);

  // Şirket haberlerini çekelim (son 7 gün)
  useEffect(() => {
    const fetchNews = async () => {
      // Bugünün tarihini ve 7 gün öncesini YYYY-MM-DD formatında belirleyelim
      const today = new Date();
      const toDate = today.toISOString().split('T')[0];
      const lastWeek = new Date(today.getTime() - 7 * 24 * 60 * 60 * 1000);
      const fromDate = lastWeek.toISOString().split('T')[0];

      try {
        const newsUrl = `https://finnhub.io/api/v1/company-news?symbol=${symbol}&from=${fromDate}&to=${toDate}&token=${FINNHUB_API_KEY}`;
        const newsResponse = await axios.get(newsUrl);
        setNews(newsResponse.data);
      } catch (error) {
        console.error('Haber verisi alınırken hata:', error);
      }
    };
    fetchNews();
  }, [symbol, FINNHUB_API_KEY]);

  if (!detailData || !quote) {
    return <div>Yükleniyor...</div>;
  }

  return (
    <div>
      <h2>{detailData.name} Detay Sayfası</h2>
      <p>Sembol: {detailData.ticker}</p>
      <p>Ülke: {detailData.country}</p>
      <p>Borsa: {detailData.exchange}</p>
      <p>Güncel Fiyat: ${quote.c}</p>
      <p>Değişim: {quote.d.toFixed(2)} ({quote.dp.toFixed(2)}%)</p>
      <h3>Haberler</h3>
      {news && news.length > 0 ? (
        <ul>
          {news.map((item) => (
            <li key={item.id}>
              <a href={item.url} target="_blank" rel="noopener noreferrer">
                {item.headline}
              </a>
              <p>{item.summary}</p>
              <small>{new Date(item.datetime * 1000).toLocaleString()}</small>
            </li>
          ))}
        </ul>
      ) : (
        <p>Haber bulunamadı.</p>
      )}
    </div>
  );
}

export default MarketDetail;
