// MarketDetail.js
import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import { FaArrowUp, FaArrowDown } from "react-icons/fa";
import "./MarketDetail.css";

const FINNHUB = "cva1t91r01qpd9sa8bi0cva1t91r01qpd9sa8big";
const API     = "http://localhost:5000/api";   // Flask backend

export default function MarketDetail() {
  const { symbol } = useParams();
  const [profile, setProfile]       = useState(null);
  const [quote, setQuote]           = useState(null);
  const [prediction, setPrediction] = useState(null);

  /* ----------- fetch helpers ----------- */
  useEffect(() => {
    axios
      .get(
        `https://finnhub.io/api/v1/stock/profile2?symbol=${symbol}&token=${FINNHUB}`
      )
      .then((r) => setProfile(r.data))
      .catch(console.error);
  }, [symbol]);

  useEffect(() => {
    axios
      .get(
        `https://finnhub.io/api/v1/quote?symbol=${symbol}&token=${FINNHUB}`
      )
      .then((r) => setQuote(r.data))
      .catch(console.error);
  }, [symbol]);

  useEffect(() => {
    axios
      .get(`${API}/predict/${symbol.toUpperCase()}`)
      .then((r) => setPrediction(r.data.direction)) // "UP" | "DOWN"
      .catch(console.error);
  }, [symbol]);

  if (!profile || !quote || !prediction) {
    return (
      <div style={{ color: "#f1f5f9", textAlign: "center", padding: "3rem" }}>
        Yükleniyor…
      </div>
    );
  }

  const isUp = prediction === "UP";

  return (
    <div className="market-wrapper">
      <div className="card">
        <h2>{profile.name} Detay Sayfası</h2>

        <div className="details-grid">
          <span>Sembol:</span> <span>{profile.ticker}</span>
          <span>Ülke:</span>   <span>{profile.country}</span>
          <span>Borsa:</span>  <span>{profile.exchange}</span>
        </div>

        <div className={`price-row ${quote.d >= 0 ? "up" : "down"}`}>
          Güncel Fiyat: ${quote.c}
          <span className="change">
            {quote.d.toFixed(2)} ({quote.dp.toFixed(2)}%)
          </span>
        </div>

      const ArrowIcon = isUp ? FaArrowUp : FaArrowDown;  // ① bileşeni seç

     <div className={`prediction ${isUp ? "up" : "down"}`}>
    Model Tahmini:&nbsp;{isUp ? "UP" : "DOWN"} <ArrowIcon />  {/* ② bileşen olarak render */}
     </div>

        <p className="note">
          *Karar ağacı modeli, geçmiş fiyat ve haber duyarlılığına göre{" "}
          {isUp ? "artış" : "azalış"} bekliyor.*
        </p>
      </div>
    </div>
  );
}
