import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const API_BASE = 'http://localhost:5000';  // Flask'in √ßalƒ±≈ütƒ±ƒüƒ± backend adresi

function MarketDetail() {
  const { symbol } = useParams();
  const [detailData, setDetailData] = useState(null);
  const [quote, setQuote] = useState(null);
  const [prediction, setPrediction] = useState(null);

  const FINNHUB_API_KEY = 'cva1t91r01qpd9sa8bi0cva1t91r01qpd9sa8big';

  // 1) ≈ûirket profilini √ßek
  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const url = `https://finnhub.io/api/v1/stock/profile2?symbol=${symbol}&token=${FINNHUB_API_KEY}`;
        const res = await axios.get(url);
        setDetailData(res.data);
      } catch (err) {
        console.error('Profil verisi alƒ±nƒ±rken hata:', err);
      }
    };
    fetchProfile();
  }, [symbol]);

  // 2) G√ºncel fiyatƒ± √ßek
  useEffect(() => {
    const fetchQuote = async () => {
      try {
        const url = `https://finnhub.io/api/v1/quote?symbol=${symbol}&token=${FINNHUB_API_KEY}`;
        const res = await axios.get(url);
        setQuote(res.data);
      } catch (err) {
        console.error('Fiyat verisi alƒ±nƒ±rken hata:', err);
      }
    };
    fetchQuote();
  }, [symbol]);

  // 3) Model tahminini √ßek (test endpoint'i)
  useEffect(() => {
    const fetchPrediction = async () => {
      console.log('üîç Fetching prediction for', symbol);
      try {
        // Test i√ßin sabit d√∂nen endpoint:
        const res = await axios.get(`${API_BASE}/api/predict/test/${symbol}`);
        console.log('‚úÖ Test predict response:', res.data);
        setPrediction(res.data);
      } catch (err) {
        console.error('‚ùå Predict error:', err);
        setPrediction({ error: true });
      }
    };
    fetchPrediction();
  }, [symbol]);

  // Hen√ºz veri gelmemi≈üse loader
  if (!detailData || !quote) {
    return <div>Y√ºkleniyor...</div>;
  }

  return (
    <div style={{ padding: 20, color: '#fff' }}>
      <h2>{detailData.name} Detay Sayfasƒ±</h2>
      <p><strong>Sembol:</strong> {detailData.ticker}</p>
      <p><strong>√úlke:</strong> {detailData.country}</p>
      <p><strong>Borsa:</strong> {detailData.exchange}</p>
      <p><strong>G√ºncel Fiyat:</strong> ${quote.c}</p>
      <p><strong>Deƒüi≈üim:</strong> {quote.d.toFixed(2)} ({quote.dp.toFixed(2)}%)</p>

      {/* Tahmin debug bloƒüu */}
      {prediction === null ? (
        <p>Tahmin y√ºkleniyor...</p>
      ) : prediction.error ? (
        <p style={{ color: 'red' }}>Tahmin alƒ±nƒ±rken hata olu≈ütu.</p>
      ) : (
        <pre style={{ background: '#333', padding: 10, borderRadius: 4 }}>
          {JSON.stringify(prediction, null, 2)}
        </pre>
      )}

      {/* Ger√ßek endpoint devreye alƒ±ndƒ±ƒüƒ±nda URL'i ≈ü√∂yle deƒüi≈ütirin:
           axios.get(`${API_BASE}/api/predict/${symbol}`) */}
    </div>
  );
}

export default MarketDetail;
