// src/components/LandingPage.js
import React, { useState, useEffect } from "react";
import { useNavigate, Link } from "react-router-dom";
import axios from "axios";
import "./LandingPage.css";

const FINNHUB_KEY = "cva1t91r01qpd9sa8bi0cva1t91r01qpd9sa8big";

/* Finnhub için tek axios instance: token’ı HEADER’da tutuyoruz  */
const finnhub = axios.create({
  baseURL: "https://finnhub.io/api/v1",
  headers: { "X-Finnhub-Token": FINNHUB_KEY },
});

export default function LandingPage() {
  const navigate = useNavigate();

  /* Arama state’leri */
  const [searchTerm, setSearchTerm] = useState("");
  const [searchResults, setSearchResults] = useState([]);

  /* Nasdaq listesini tek sefer çekiyoruz */
  const [nasdaqSymbols, setNasdaqSymbols] = useState([]);

  /* Yardımcı – slash varyasyonu */
  const insertSlash = (txt) => {
    const t = txt.trim().toUpperCase();
    return t.length === 6 || t.length === 7 ? t.slice(0, 3) + "/" + t.slice(3) : t;
  };

  /* Finnhub search */
  const fetchSymbols = async (q) => {
    const { data } = await finnhub.get("/search", { params: { q } });
    return data.result || [];
  };

  /* Nasdaq symbol list */
  useEffect(() => {
    (async () => {
      try {
        const { data } = await finnhub.get("/stock/symbol", { params: { exchange: "US" } });
        const list = data.filter((r) => r.mic === "XNAS").map((r) => r.symbol);
        setNasdaqSymbols(list);
      } catch (e) {
        console.error("Nasdaq sembol listesi alınamadı:", e);
      }
    })();
  }, []);

  /* Arama debounced */
  useEffect(() => {
    if (searchTerm.trim().length < 2) {
      setSearchResults([]);
      return;
    }

    const timer = setTimeout(async () => {
      try {
        const term = searchTerm.trim().toUpperCase();

        const direct = await fetchSymbols(term);
        const slash  = term !== insertSlash(term) ? await fetchSymbols(insertSlash(term)) : [];

        const merged = [...direct, ...slash];
        const uniq = [];
        const seen = new Set();
        merged.forEach((it) => {
          if (!seen.has(it.symbol)) {
            seen.add(it.symbol);
            uniq.push(it);
          }
        });

        const onlyNasdaq =
          nasdaqSymbols.length > 0
            ? uniq.filter((it) => nasdaqSymbols.includes(it.symbol))
            : uniq;

        setSearchResults(onlyNasdaq);
      } catch (e) {
        console.error("Finnhub API:", e);
        setSearchResults([]);
      }
    }, 500);

    return () => clearTimeout(timer);
  }, [searchTerm, nasdaqSymbols]);

  /* JSX */
  return (
    <div className="landing-container">
      <header className="landing-header">
        <h2 className="logo-text">Finansal Analiz</h2>
        <nav className="navbar">
          {/* … nav linkleri … */}
        </nav>
      </header>

      <section className="hero-section">
        <div className="hero-content">
          <h1 className="hero-title">Finansal Analiz Uygulaması</h1>
          <p className="hero-subtitle">Gelir–gider takibi ve yatırım önerileri.</p>
          <button className="cta-button" onClick={() => navigate("/register")}>
            Hemen Başla
          </button>
        </div>

        {/* Piyasa arama paneli */}
        <aside className="markets-sidebar">
          <h3>Piyasa Arama</h3>
          <div className="search-bar">
            <input
              type="text"
              placeholder="Hisse ara..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
            />
          </div>

          <ul className="market-list">
            {searchResults.length === 0 && searchTerm.trim().length >= 2 ? (
              <li className="market-item">Sonuç bulunamadı.</li>
            ) : (
              searchResults.map((it) => (
                <li key={it.symbol} className="market-item">
                  <Link to={`/market/${it.symbol}`}>
                    {it.symbol} — {it.description}
                  </Link>
                </li>
              ))
            )}
          </ul>
        </aside>
      </section>

      {/* … özellikler, footer bölümleri … */}
    </div>
  );
}
