// src/components/LandingPage.js
import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "./LandingPage.css";

const FINNHUB_BASE = "https://finnhub.io/api/v1";
const FINNHUB_KEY  = "cva1t91r01qpd9sa8bi0cva1t91r01qpd9sa8big";

export default function LandingPage() {
  const navigate = useNavigate();
  const [symbol, setSymbol] = useState("");
  const [quote, setQuote]   = useState(null);
  const [error, setError]   = useState(false);

  const handleChange = e => {
    setSymbol(e.target.value.trim().toUpperCase());
    setQuote(null);
    setError(false);
  };

  const handleSearch = () => {
    if (!symbol) return;
    // Doğru Finnhub URL’i:
    axios
      .get(`${FINNHUB_BASE}/quote`, {
        params: { symbol, token: FINNHUB_KEY }
      })
      .then(res => {
        if (res.data && res.data.c) {
          setQuote(res.data);
          setError(false);
        } else {
          setQuote(null);
          setError(true);
        }
      })
      .catch(() => {
        setQuote(null);
        setError(true);
      });
  };

  return (
    <div className="landing-wrapper">
      <header className="landing-header">
        Finansal Analiz
        <nav>
          <a href="#ozellikler">Özellikler</a>
          <a href="#teknolojiler">Teknolojiler</a>
          <a href="#hakkimizda">Hakkımızda</a>
          <a href="#iletisim">İletişim</a>
          <button onClick={() => navigate("/login")}>Giriş Yap</button>
          <button onClick={() => navigate("/register")}>Kayıt Ol</button>
        </nav>
      </header>

      <main className="landing-main">
        <section className="landing-intro">
          <h1>Bitirme Projesi: Finansal Analiz Uygulaması</h1>
          <p>
            Gelir-gider takibi, bütçe yönetimi ve yatırım önerileri tek platformda.
            Makine öğrenmesi destekli analizlerle daha bilinçli finansal kararlar alın!
          </p>
          <button onClick={() => navigate("/dashboard")}>Hemen Başla</button>
        </section>

        <aside className="landing-market">
          <h2>Piyasalar (Finnhub)</h2>
          <div className="market-search">
            <input
              type="text"
              value={symbol}
              onChange={handleChange}
              placeholder="Hisse sembolü (örn: AAPL)"
            />
            <button onClick={handleSearch}>Ara</button>
          </div>

          {quote && !error ? (
            <div className="quote-result">
              <p>Fiyat: ${quote.c}</p>
              <p>
                Değişim: {quote.d.toFixed(2)} ({quote.dp.toFixed(2)}%)
              </p>
            </div>
          ) : error ? (
            <p className="no-result">Sonuç bulunamadı.</p>
          ) : null}
        </aside>
      </main>
    </div>
  );
}
