import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './LandingPage.css';

function LandingPage() {
  // Burada örnek olarak 4 sembol belirledik (IBM, AAPL, BTC/USD, ETH/USD).
  // İstediğiniz sembolleri ekleyebilirsiniz (ör. TSLA, GOOGL, DOGE/USD vs.).
  const [symbols] = useState(['IBM', 'AAPL', 'BTCUSD', 'ETHUSD']);
  const [marketData, setMarketData] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');

  // Alpha Vantage API Key (ücretsiz hesapla elde edilir)
  const ALPHAVANTAGE_API_KEY = 'R9QMG5MRESRAFFYX';

  // useEffect içinde, sayfa ilk yüklendiğinde semboller için verileri çekiyoruz.
  // Ayrıca her sembol için "GLOBAL_QUOTE" endpoint'ine istek atıyoruz (hisse senetleri ve kripto paralar için benzer).
  useEffect(() => {
    // Tek seferde tüm sembolleri toplu çekmek isterseniz, "Batch Stock Market Quotes" endpoint'i de kullanabilirsiniz.
    // Örneğin: https://www.alphavantage.co/query?function=BATCH_STOCK_QUOTES&symbols=IBM,AAPL&apikey=...
    // Ancak kripto için farklı endpoint gerekebiliyor. Bu örnekte tek tek istek yapıyoruz.

    async function fetchMarketData() {
      const results = [];

      for (let sym of symbols) {
        try {
          // Hisse senedi ve kripto sembollerinin global quote endpoint'iyle çalışması genellikle benzer.
          // Kripto için bazen "CURRENCY_EXCHANGE_RATE" endpoint'i kullanmak gerekebilir.
          // Örneğin: https://www.alphavantage.co/query?function=CURRENCY_EXCHANGE_RATE&from_currency=BTC&to_currency=USD
          const url = `https://www.alphavantage.co/query?function=GLOBAL_QUOTE&symbol=${sym}&apikey=${ALPHAVANTAGE_API_KEY}`;
          const response = await axios.get(url);

          // Alpha Vantage yanıtı, "Global Quote" verilerini "Global Quote" anahtarı altında döndürür.
          // Örnek: response.data["Global Quote"] = { "01. symbol": "IBM", "05. price": "123.45", ... }
          const quote = response.data['Global Quote'];
          if (quote) {
            const symbolName = quote['01. symbol'] || sym;
            const price = quote['05. price'];
            const changePercent = quote['10. change percent'];

            results.push({
              symbol: symbolName,
              price: price ? parseFloat(price).toFixed(2) : 'N/A',
              change: changePercent || 'N/A'
            });
          } else {
            // Herhangi bir veri gelmezse
            results.push({
              symbol: sym,
              price: 'N/A',
              change: 'N/A'
            });
          }
        } catch (err) {
          console.error('API Hatası:', err);
          results.push({
            symbol: sym,
            price: 'N/A',
            change: 'N/A'
          });
        }
      }

      setMarketData(results);
    }

    fetchMarketData();
  }, [symbols, ALPHAVANTAGE_API_KEY]);

  // Arama filtresi
  const filteredData = marketData.filter((item) =>
    item.symbol.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
