// src/components/MarketDetail.js
import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

function MarketDetail() {
  const { symbol } = useParams();
  const [detailData, setDetailData] = useState(null);

  // Örnek olarak Finnhub'dan sembol ile ilgili ekstra bilgi çekebilirsiniz
  useEffect(() => {
    const fetchDetailData = async () => {
      try {
        const FINNHUB_API_KEY = 'cva1t91r01qpd9sa8bi0cva1t91r01qpd9sa8big';
        const url = `https://finnhub.io/api/v1/stock/profile2?symbol=${symbol}&token=${FINNHUB_API_KEY}`;
        const response = await axios.get(url);
        setDetailData(response.data);
      } catch (error) {
        console.error('Detay verisi alınırken hata oluştu:', error);
      }
    };

    fetchDetailData();
  }, [symbol]);

  if (!detailData) {
    return <div>Yükleniyor...</div>;
  }

  return (
    <div>
      <h2>{detailData.name} Detay Sayfası</h2>
      <p>Sembol: {detailData.ticker}</p>
      <p>Ülke: {detailData.country}</p>
      <p>Borsa: {detailData.exchange}</p>
      {/* İstediğiniz diğer bilgileri burada gösterin */}
    </div>
  );
}

export default MarketDetail;
