import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate, Link } from 'react-router-dom';
import './LandingPage.css';

function NewsSlider({ token }) {
  const [news, setNews] = useState([]);

  useEffect(() => {
    const fetchNews = async () => {
      try {
        // Genel haberler endpointi; isterseniz company-news endpointini de kullanabilirsiniz.
        const url = `https://finnhub.io/api/v1/news?category=general&token=${token}`;
        const response = await axios.get(url);
        setNews(response.data);
      } catch (error) {
        console.error('Haberler alınırken hata:', error);
      }
    };

    fetchNews();
  }, [token]);

  return (
    <div className="news-slider">
      {news.length > 0 ? (
        news.map((item) => (
          <div key={item.id} className="news-card">
            <a href={item.url} target="_blank" rel="noopener noreferrer">
              <h4>{item.headline}</h4>
            </a>
            <p>{item.summary}</p>
            <small>{new Date(item.datetime * 1000).toLocaleString()}</small>
          </div>
        ))
      ) : (
        <p>Haber bulunamadı.</p>
      )}
    </div>
  );
}

function LandingPage() {
  // Arama için state
  const [searchTerm, setSearchTerm] = useState('');
  const [searchResults, setSearchResults] = useState([]);
  // Nasdaq sembollerini saklamak için state
  const [nasdaqSymbols, setNasdaqSymbols] = useState([]);

  // Finnhub API Key
  const FINNHUB_API_KEY = 'cva1t91r01qpd9sa8bi0cva1t91r01qpd9sa8big';

  // useNavigate ile yönlendirme
  const navigate = useNavigate();

  // Çoklu varyasyon araması için yardımcı fonksiyon
  const insertSlash = (term) => {
    const t = term.toUpperCase().trim();
    if (t.length === 6) return t.slice(0, 3) + '/' + t.slice(3);
    if (t.length === 7) return t.slice(0, 3) + '/' + t.slice(3);
    return t;
  };

  // Finnhub API üzerinden sembol arama fonksiyonu
  const fetchFinnhubSymbols = async (term) => {
    const url = `https://finnhub.io/api/v1/search?q=${encodeURIComponent(term)}&token=${FINNHUB_API_KEY}`;
    const response = await axios.get(url);
    return response.data.result || [];
  };

  // Component yüklendiğinde Nasdaq sembollerini çekiyoruz.
  useEffect(() => {
    const fetchNasdaqSymbols = async () => {
      try {
        const url = `https://finnhub.io/api/v1/stock/symbol?exchange=US&token=${FINNHUB_API_KEY}`;
        const response = await axios.get(url);
        // Nasdaq için "mic" değeri "XNAS" olanları filtreliyoruz.
        const nasdaqList = response.data
          .filter((item) => item.mic === 'XNAS')
          .map((item) => item.symbol);
        setNasdaqSymbols(nasdaqList);
      } catch (error) {
        console.error('Nasdaq sembolleri alınırken hata oluştu:', error);
      }
    };

    fetchNasdaqSymbols();
  }, [FINNHUB_API_KEY]);

  // Arama yaparken Nasdaq listesine göre filtreleme
  useEffect(() => {
    if (searchTerm.trim().length < 2) {
      setSearchResults([]);
      return;
    }
    const doMultiSearch = async () => {
      try {
        const trimmed = searchTerm.trim().toUpperCase();
        const directResults = await fetchFinnhubSymbols(trimmed);
        let slashResults = [];
        const slashTerm = insertSlash(trimmed);
        if (slashTerm !== trimmed) {
          slashResults = await fetchFinnhubSymbols(slashTerm);
        }
        const combined = [...directResults, ...slashResults];
        const uniqueSet = new Set();
        const uniqueResults = [];
        for (let item of combined) {
          const key = item.symbol;
          if (!uniqueSet.has(key)) {
            uniqueSet.add(key);
            uniqueResults.push(item);
          }
        }
        const finalFiltered = uniqueResults.filter((item) =>
          item.symbol.toUpperCase().includes(trimmed)
        );
        const nasdaqFiltered =
          nasdaqSymbols.length > 0
            ? finalFiltered.filter((item) => nasdaqSymbols.includes(item.symbol))
            : [];
        setSearchResults(nasdaqFiltered);
      } catch (err) {
        console.error('Finnhub API Hatası:', err);
        setSearchResults([]);
      }
    };

    const delayDebounceFn = setTimeout(() => {
      doMultiSearch();
    }, 500);
    return () => clearTimeout(delayDebounceFn);
  }, [searchTerm, FINNHUB_API_KEY, nasdaqSymbols]);

  return (
    <div className="landing-container">
      {/* HEADER / NAVBAR */}
      <header className="landing-header">
        <div className="logo-area">
          <h2 className="logo-text">Finansal Analiz</h2>
        </div>
        <nav className="navbar">
          <ul>
            <li><a href="#features">Özellikler</a></li>
            <li><a href="#technology">Teknolojiler</a></li>
            <li><a href="#about">Hakkımızda</a></li>
            <li><a href="#contact">İletişim</a></li>
            <li><a href="/Login">Giriş Yap</a></li>
            <li><a href="/Register">Kayıt Ol</a></li>
          </ul>
        </nav>
      </header>

      {/* HERO BÖLÜMÜ */}
      <section className="hero-section">
        <div className="hero-content">
          <h1 className="hero-title">
            Bitirme Projesi: Finansal Analiz Uygulaması
          </h1>
          <p className="hero-subtitle">
            Gelir-gider takibi, bütçe yönetimi ve yatırım önerileri tek platformda. Makine
            öğrenmesi destekli analizlerle daha bilinçli finansal kararlar alın!
          </p>
          <button className="cta-button" onClick={() => navigate('/register')}>
            Hemen Başla
          </button>
        </div>
      </section>

      {/* Haber Slider Bölümü - Hero section’un hemen altında */}
      <section className="news-slider-section">
        <h3>Son Haberler</h3>
        <NewsSlider token={FINNHUB_API_KEY} />
      </section>

      {/* Piyasalar Bölümü */}
      <aside className="markets-sidebar">
        <h3>Piyasalar (Finnhub)</h3>
        <div className="search-bar">
          <input
            type="text"
            placeholder="Hisse veya Kripto Ara..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
        </div>
        <ul className="market-list">
          {searchResults.length === 0 && searchTerm.trim().length >= 2 ? (
            <li className="market-item">Sonuç bulunamadı.</li>
          ) : (
            searchResults.map((item, index) => (
              <li key={index} className="market-item">
                <Link to={`/market/${item.symbol}`}>
                  <span className="market-name">
                    {item.symbol} - {item.description}
                  </span>
                </Link>
              </li>
            ))
          )}
        </ul>
      </aside>

      {/* Diğer bölümler: Özellikler, Teknolojiler, Hakkımızda, Footer */}
      <section id="features" className="features-section">
        <h2>Öne Çıkan Özellikler</h2>
        <div className="features-container">
          <div className="feature-card">
            <h3>Finansal Durum Görselleştirme</h3>
            <p>
              Gelir ve giderlerinizi grafikler ve tablolarla takip edin. Harcamalarınızı
              kategorize ederek bütçenizi daha iyi yönetin.
            </p>
          </div>
          <div className="feature-card">
            <h3>Gelir-Gider Takibi</h3>
            <p>
              Günlük/aylık giderlerinizi kaydedin, bütçe hedefleri belirleyin ve geçmiş
              harcama trendlerinizi inceleyin.
            </p>
          </div>
          <div className="feature-card">
            <h3>Yatırım Önerileri</h3>
            <p>
              Makine öğrenmesi modelleriyle potansiyel yatırım fırsatlarını analiz ederek
              size özel öneriler sunar.
            </p>
          </div>
          <div className="feature-card">
            <h3>Makine Öğrenmesi</h3>
            <p>
              Scikit-Learn veya TensorFlow kullanarak finansal tahminler ve risk analizleri
              yapın, geleceğe dair içgörü kazanın.
            </p>
          </div>
        </div>
      </section>

      <section id="technology" className="technology-section">
        <h2>Kullanılan Teknolojiler</h2>
        <div className="tech-grid">
          <div className="tech-item">
            <img
              src="https://upload.wikimedia.org/wikipedia/commons/6/6a/JavaScript-logo.png"
              alt="JS Logo"
            />
            <span>JavaScript</span>
          </div>
          <div className="tech-item">
            <img
              src="https://upload.wikimedia.org/wikipedia/commons/a/a7/React-icon.svg"
              alt="React Logo"
            />
            <span>React.js</span>
          </div>
          <div className="tech-item">
            <img
              src="https://static-00.iconduck.com/assets.00/programming-language-flask-icon-2048x1826-wf5k5ugs.png"
              alt="Flask Logo"
            />
            <span>Flask (Python)</span>
          </div>
          <div className="tech-item">
            <img
              src="https://upload.wikimedia.org/wikipedia/commons/2/29/Postgresql_elephant.svg"
              alt="PostgreSQL Logo"
            />
            <span>PostgreSQL</span>
          </div>
          <div className="tech-item">
            <img
              src="https://upload.wikimedia.org/wikipedia/commons/0/05/Scikit_learn_logo_small.svg"
              alt="Scikit-Learn Logo"
            />
            <span>Scikit-Learn</span>
          </div>
        </div>
      </section>

      <section id="about" className="about-section">
        <h2>Hakkımızda</h2>
        <p>
          Bu proje, üniversite bitirme projesi kapsamında geliştirilmiş bir finansal analiz
          uygulamasıdır. Amacımız, kullanıcıların finansal verilerini etkin bir şekilde
          yönetmelerini ve yatırımlarını daha bilinçli yapmalarını sağlamaktır.
        </p>
      </section>

      <footer id="contact" className="landing-footer">
        <p>© 2025 Finansal Analiz Uygulaması. Tüm hakları saklıdır.</p>
        <p>
          İletişim: <a href="mailto:info@finansalanaliz.com">info@finansalanaliz.com</a>
        </p>
      </footer>
    </div>
  );
}

export default LandingPage;
