import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './LandingPage.css';

function LandingPage() {
  // Arama için state
  const [searchTerm, setSearchTerm] = useState('');
  const [searchResults, setSearchResults] = useState([]);

  // Finnhub API Key
  const FINNHUB_API_KEY = 'cva1t91r01qpd9sa8bi0cva1t91r01qpd9sa8big';

  // Slash ekleme fonksiyonu (örnek)
  const insertSlash = (term) => {
    const t = term.toUpperCase().trim();
    if (t.length === 6) {
      // Ör. "BTCTRY" -> "BTC/TRY"
      return t.slice(0, 3) + '/' + t.slice(3);
    }
    if (t.length === 7) {
      // Ör. "BTCUSDT" -> "BTC/USDT"
      return t.slice(0, 3) + '/' + t.slice(3);
    }
    return t;
  };

  // Arama fonksiyonu: Finnhub'ta sembol arar
  const fetchFinnhubSymbols = async (term) => {
    const url = `https://finnhub.io/api/v1/search?q=${encodeURIComponent(term)}&token=${FINNHUB_API_KEY}`;
    const response = await axios.get(url);
    return response.data.result || [];
  };

  useEffect(() => {
    if (searchTerm.trim().length < 2) {
      setSearchResults([]);
      return;
    }

    const doMultiSearch = async () => {
      try {
        const trimmed = searchTerm.trim().toUpperCase();

        // 1) Doğrudan kullanıcı girdiği terimle arama
        const directResults = await fetchFinnhubSymbols(trimmed);

        // 2) Slash eklenmiş varyasyonla arama (isteğe bağlı)
        let slashResults = [];
        const slashTerm = insertSlash(trimmed);
        if (slashTerm !== trimmed) {
          slashResults = await fetchFinnhubSymbols(slashTerm);
        }

        // 3) Sonuçları birleştir
        const combined = [...directResults, ...slashResults];

        // 4) Duplicate sembolleri temizle (ör. Set kullanarak)
        const uniqueSet = new Set();
        const uniqueResults = [];
        for (let item of combined) {
          const key = item.symbol;
          if (!uniqueSet.has(key)) {
            uniqueSet.add(key);
            uniqueResults.push(item);
          }
        }

        // 5) İsteğe bağlı olarak arama terimiyle "includes" filtresi
        const finalFiltered = uniqueResults.filter((item) =>
          item.symbol.toUpperCase().includes(trimmed)
        );

        setSearchResults(finalFiltered);
      } catch (err) {
        console.error('Finnhub API Hatası:', err);
        setSearchResults([]);
      }
    };

    const delayDebounceFn = setTimeout(() => {
      doMultiSearch();
    }, 500);

    return () => clearTimeout(delayDebounceFn);
  }, [searchTerm]);

  return (
    <div className="landing-container">
      {/* Üst Kısım (Header / Navbar) */}
      <header className="landing-header">
        <div className="logo-area">
          <h2 className="logo-text">Finansal Analiz</h2>
        </div>
        <nav className="navbar">
          <ul>
            <li><a href="#features">Özellikler</a></li>
            <li><a href="#technology">Teknolojiler</a></li>
            <li><a href="#about">Hakkımızda</a></li>
            <li><a href="#contact">İletişim</a></li>
            <li><a href="/Login">Giriş Yap</a></li>
            <li><a href="/Register">Kayıt Ol</a></li>
          </ul>
        </nav>
      </header>

      {/* Hero Bölümü */}
      <section className="hero-section">
        <div className="hero-content">
          <h1 className="hero-title">
            Bitirme Projesi: Finansal Analiz Uygulaması
          </h1>
          <p className="hero-subtitle">
            Gelir-gider takibi, bütçe yönetimi ve yatırım önerileri tek platformda.
            Makine öğrenmesi destekli analizlerle daha bilinçli finansal kararlar alın!
          </p>
          <button className="cta-button">Hemen Başla</button>
        </div>

        {/* Sağ Kısım: Piyasalar Bölümü (Çoklu Arama + Kaydırma) */}
        <aside className="markets-sidebar">
          <h3>Piyasalar (Finnhub)</h3>
          <div className="search-bar">
            <input
              type="text"
              placeholder="Hisse veya Kripto Ara..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
            />
          </div>
          <ul className="market-list">
            {searchResults.length === 0 && searchTerm.trim().length >= 2 ? (
              <li className="market-item">Sonuç bulunamadı.</li>
            ) : (
              searchResults.map((item, index) => (
                <li key={index} className="market-item">
                  <span className="market-name">
                    {item.symbol} - {item.description}
                  </span>
                </li>
              ))
            )}
          </ul>
        </aside>
      </section>

      {/* Özellikler, Teknolojiler, Hakkımızda, Footer */}
      {/* ... */}
    </div>
  );
}

export default LandingPage;
