// src/components/LandingPage.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate, Link } from 'react-router-dom';
import './LandingPage.css';

const FINNHUB_KEY = 'cva1t91r01qpd9sa8bi0cva1t91r01qpd9sa8big';

const finnhub = axios.create({
  baseURL: '/finnhub',
  headers: { 'X-Finnhub-Token': FINNHUB_KEY },
});

export default function LandingPage() {
  const [searchTerm, setSearchTerm] = useState('');
  const [searchResults, setSearchResults] = useState([]);
  const [nasdaqSymbols, setNasdaqSymbols] = useState([]);
  const navigate = useNavigate();

  // “ABCDEF → ABC/DEF” varyasyonu
  const insertSlash = (term) => {
    const t = term.trim().toUpperCase();
    return t.length === 6 || t.length === 7 ? `${t.slice(0, 3)}/${t.slice(3)}` : t;
  };

  // Nasdaq sembollerini bir kez çek
  useEffect(() => {
    const fetchNasdaq = async () => {
      try {
        const { data } = await finnhub.get('/stock/symbol', { params: { exchange: 'US' } });
        const list = data.filter((s) => s.mic === 'XNAS').map((s) => s.symbol);
        setNasdaqSymbols(list);
      } catch (err) {
        console.error('Nasdaq sembolleri alınamadı:', err);
      }
    };
    fetchNasdaq();
  }, []);

  // Arama (debounce)
  useEffect(() => {
    if (searchTerm.trim().length < 2) {
      setSearchResults([]);
      return;
    }
    const timer = setTimeout(async () => {
      try {
        const term = searchTerm.trim().toUpperCase();

        // doğrudan ve slash’li sorguları paralel getir
        const [directRes, slashRes] = await Promise.all([
          finnhub.get('/search', { params: { q: term } }),
          insertSlash(term) !== term
            ? finnhub.get('/search', { params: { q: insertSlash(term) } })
            : Promise.resolve({ data: { result: [] } }),
        ]);

        const combined = [...directRes.data.result, ...slashRes.data.result];
        const uniq = new Map();
        combined.forEach((i) => uniq.set(i.symbol, i)); // sembole göre benzersiz
        const filtered = [...uniq.values()].filter((i) => nasdaqSymbols.includes(i.symbol));

        setSearchResults(filtered);
      } catch (err) {
        console.error('Arama hatası:', err);
        setSearchResults([]);
      }
    }, 500);
    return () => clearTimeout(timer);
  }, [searchTerm, nasdaqSymbols]);

  return (
    <div className="landing-container">
      {/* ========== HEADER ========= */}
      <header className="landing-header">
        <div className="logo-area">
          <h2 className="logo-text">Finansal Analiz</h2>
        </div>

        <nav className="navbar">
          <ul>
            <li><a href="#features">Özellikler</a></li>
            <li><a href="#technology">Teknolojiler</a></li>
            <li><a href="#about">Hakkımızda</a></li>
            <li><a href="#contact">İletişim</a></li>
            <li><Link to="/login">Giriş Yap</Link></li>
            <li><Link to="/register">Kayıt Ol</Link></li>
          </ul>
        </nav>
      </header>

      {/* ========== HERO + PİYASALAR ========= */}
      <section className="hero-section">
        <div className="hero-content">
          <h1 className="hero-title">
            Bitirme Projesi: Finansal Analiz Uygulaması
          </h1>
          <p className="hero-subtitle">
            Gelir-gider takibi, bütçe yönetimi ve yatırım önerileri tek platformda.
            Makine öğrenmesi destekli analizlerle daha bilinçli finansal kararlar alın!
          </p>
          <button className="cta-button" onClick={() => navigate('/register')}>
            Hemen Başla
          </button>
        </div>

        <aside className="markets-sidebar">
          <h3>Piyasalar (Finnhub)</h3>

          <div className="search-bar">
            <input
              type="text"
              className="market-search"
              placeholder="Hisse sembolü ara…"
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
            />
          </div>

          <ul className="market-list">
            {searchResults.length === 0 && searchTerm.trim().length >= 2 ? (
              <li className="market-item">Sonuç bulunamadı.</li>
            ) : (
              searchResults.map((item) => (
                <li key={item.symbol} className="market-item">
                  <Link to={`/market/${item.symbol}`}>
                    {item.symbol} – {item.description}
                  </Link>
