// src/components/LandingPage.js
import React, { useState, useEffect } from "react";
import { useNavigate, Link } from "react-router-dom";
import axios from "axios";
import "./LandingPage.css";

const FINNHUB_KEY = "cva1t91r01qpd9sa8bi0cva1t91r01qpd9sa8big";

/* -------- ortak Finnhub axios instance -------- */
const finnhub = axios.create({
  baseURL: "https://finnhub.io/api/v1",
  headers: { "X-Finnhub-Token": FINNHUB_KEY },
});

export default function LandingPage() {
  const navigate = useNavigate();

  /* Arama state’leri */
  const [searchTerm, setSearchTerm] = useState("");
  const [searchResults, setSearchResults] = useState([]);

  /* Önceden çekilen Nasdaq sembolleri */
  const [nasdaqSymbols, setNasdaqSymbols] = useState([]);

  /* Slash varyasyonu (ör. ABCDEF → ABC/DEF) */
  const insertSlash = (txt) => {
    const t = txt.trim().toUpperCase();
    return t.length === 6 || t.length === 7 ? t.slice(0, 3) + "/" + t.slice(3) : t;
  };

  /* Finnhub search helper */
  const fetchFinnhubSymbols = async (term) => {
    const { data } = await finnhub.get("/search", { params: { q: term } });
    return data.result || [];
  };

  /* Nasdaq sembol listesini bir kez al */
  useEffect(() => {
    const fetchNasdaq = async () => {
      try {
        const { data } = await finnhub.get("/stock/symbol", {
          params: { exchange: "US" },
        });
        const list = data.filter((x) => x.mic === "XNAS").map((x) => x.symbol);
        setNasdaqSymbols(list);
      } catch (e) {
        console.error("Nasdaq sembolleri alınamadı:", e);
      }
    };
    fetchNasdaq();
  }, []);

  /* Arama debounced */
  useEffect(() => {
    if (searchTerm.trim().length < 2) {
      setSearchResults([]);
      return;
    }

    const doSearch = async () => {
      try {
        const term = searchTerm.trim().toUpperCase();

        /* 1) doğrudan arama */
        const direct = await fetchFinnhubSymbols(term);

        /* 2) slash varyasyonu */
        const slashTerm = insertSlash(term);
        const slash =
          slashTerm !== term ? await fetchFinnhubSymbols(slashTerm) : [];

        /* 3) birleştir, benzersiz kıl */
        const map = new Map();
        [...direct, ...slash].forEach((it) => map.set(it.symbol, it));

        /* 4) sadece inputu içerenler */
        const filtered = [...map.values()].filter((it) =>
          it.symbol.includes(term)
        );

        /* 5) yalnızca Nasdaq */
        const onlyNasdaq = filtered.filter((it) =>
          nasdaqSymbols.includes(it.symbol)
        );

        setSearchResults(onlyNasdaq);
      } catch (err) {
        console.error("Finnhub API:", err);
        setSearchResults([]);
      }
    };

    const id = setTimeout(doSearch, 500);
    return () => clearTimeout(id);
  }, [searchTerm, nasdaqSymbols]);

  /* ---------- JSX ---------- */
  return (
    <div className="landing-container">
      {/* HEADER */}
      <header className="landing-header">
        <div className="logo-area">
          <h2 className="logo-text">Finansal Analiz</h2>
        </div>
        <nav className="navbar">
          <ul>
            <li><a href="#features">Özellikler</a></li>
            <li><a href="#technology">Teknolojiler</a></li>
            <li><a href="#about">Hakkımızda</a></li>
            <li><a href="#contact">İletişim</a></li>
            <li><a href="/Login">Giriş Yap</a></li>
            <li><a href="/Register">Kayıt Ol</a></li>
          </ul>
        </nav>
      </header>

      {/* HERO + PİYASALAR */}
      <section className="hero-section">
        {/* Sol */}
        <div className="hero-content">
          <h1 className="hero-title">Bitirme Projesi: Finansal Analiz</h1>
          <p className="hero-subtitle">
            Gelir–gider takibi, bütçe yönetimi ve yatırım önerileri tek
            platformda. Makine öğrenmesi destekli analizlerle daha bilinçli
            finansal kararlar alın!
          </p>
          <button className="cta-button" onClick={() => navigate("/register")}>
            Hemen Başla
          </button>
        </div>

        {/* Sağ – Arama */}
        <aside className="markets-sidebar">
          <h3>Piyasalar (Finnhub)</h3>
          <div className="search-bar">
            <input
              type="text"
              placeholder="Hisse sembolü ara…"
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
            />
          </div>

          <ul className="market-list">
            {searchResults.length === 0 && searchTerm.trim().length >= 2 ? (
              <li className="market-item">Sonuç bulunamadı.</li>
            ) : (
              searchResults.map((it) => (
                <li key={it.symbol} className="market-item">
                  <Link to={`/market/${it.symbol}`}>
                    {it.symbol} – {it.description}
                  </Link>
                </li>
              ))
            )}
          </ul>
        </aside>
      </section>

      {/* Diğer Bölümler (features, tech, about, footer) – mevcut kodunuz değişmeden kalabilir */}
    </div>
  );
}
