import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const API_BASE = 'http://localhost:5000';  // Backend adresiniz

function MarketDetail() {
  const { symbol } = useParams();
  const [detailData, setDetailData] = useState(null);
  const [quote, setQuote] = useState(null);
  const [prediction, setPrediction] = useState(null);

  const FINNHUB_API_KEY = 'cva1t91r01qpd9sa8bi0cva1t91r01qpd9sa8big';

  // 1) Åžirket profilini Ã§ek
  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const url = `https://finnhub.io/api/v1/stock/profile2?symbol=${symbol}&token=${FINNHUB_API_KEY}`;
        const res = await axios.get(url);
        setDetailData(res.data);
      } catch (err) {
        console.error('Profil verisi alÄ±nÄ±rken hata:', err);
      }
    };
    fetchProfile();
  }, [symbol]);

  // 2) GÃ¼ncel fiyatÄ± Ã§ek
  useEffect(() => {
    const fetchQuote = async () => {
      try {
        const url = `https://finnhub.io/api/v1/quote?symbol=${symbol}&token=${FINNHUB_API_KEY}`;
        const res = await axios.get(url);
        setQuote(res.data);
      } catch (err) {
        console.error('Fiyat verisi alÄ±nÄ±rken hata:', err);
      }
    };
    fetchQuote();
  }, [symbol]);

  // 3) Model tahminini Ã§ek
  useEffect(() => {
    const fetchPrediction = async () => {
      try {
        const res = await axios.get(`${API_BASE}/api/predict/${symbol}`);
        console.log('ðŸ”® Predict response:', res.data);
        setPrediction(res.data);
      } catch (err) {
        console.error('Tahmin verisi alÄ±nÄ±rken hata:', err);
      }
    };
    fetchPrediction();
  }, [symbol]);

  // HenÃ¼z veri gelmemiÅŸse loader
  if (!detailData || !quote) {
    return <div>YÃ¼kleniyor...</div>;
  }

  return (
    <div style={{ padding: 20, color: '#fff' }}>
      <h2>{detailData.name} Detay SayfasÄ±</h2>
      <p><strong>Sembol:</strong> {detailData.ticker}</p>
      <p><strong>Ãœlke:</strong> {detailData.country}</p>
      <p><strong>Borsa:</strong> {detailData.exchange}</p>
      <p><strong>GÃ¼ncel Fiyat:</strong> ${quote.c}</p>
      <p><strong>DeÄŸiÅŸim:</strong> {quote.d.toFixed(2)} ({quote.dp.toFixed(2)}%)</p>

      {/* Tahmin debug bloÄŸu */}
      {prediction === null ? (
        <p>Tahmin gelmedi veya yÃ¼kleniyor...</p>
      ) : (
        <pre style={{ background: '#333', padding: 10, borderRadius: 4 }}>
          {JSON.stringify(prediction, null, 2)}
        </pre>
      )}

      {/* Ä°leride burayÄ± gerÃ§ek kutu ile deÄŸiÅŸtirin */}
    </div>
  );
}

export default MarketDetail;
