import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate, Link } from 'react-router-dom';
import './LandingPage.css';

function LandingPage() {
  const [searchTerm, setSearchTerm]       = useState('');
  const [searchResults, setSearchResults] = useState([]);
  const [nasdaqSymbols, setNasdaqSymbols] = useState([]);
  const navigate = useNavigate();
  const FINNHUB_API_KEY = process.env.REACT_APP_FINNHUB_API_KEY;

  const insertSlash = (term) => {
    const t = term.toUpperCase().trim();
    if (t.length === 6 || t.length === 7) {
      return t.slice(0,3) + '/' + t.slice(3);
    }
    return t;
  };

  // Proxy endpoint'e yönlendiriyoruz
  const fetchFinnhubSymbols = async (term) => {
    const res = await axios.get(`/api/market/search?q=${encodeURIComponent(term)}`);
    return res.data.result || [];
  };

  useEffect(() => {
    const fetchNasdaqSymbols = async () => {
      try {
        const res = await axios.get('/api/market/symbols?exchange=US');
        // only XNAS items
        const list = res.data
          .filter((i) => i.mic === 'XNAS')
          .map((i) => i.symbol);
        setNasdaqSymbols(list);
      } catch (err) {
        console.error('Nasdaq sembolleri alınırken hata:', err);
      }
    };
    fetchNasdaqSymbols();
  }, []);

  useEffect(() => {
    if (searchTerm.trim().length < 2) {
      setSearchResults([]);
      return;
    }

    const doMultiSearch = async () => {
      try {
        const t = searchTerm.trim().toUpperCase();
        const direct  = await fetchFinnhubSymbols(t);
        const slashT  = insertSlash(t);
        const slash   = slashT !== t ? await fetchFinnhubSymbols(slashT) : [];
        const combined = [...direct, ...slash];

        // dedupe & filter
        const uniq = Array.from(new Map(combined.map(i => [i.symbol, i])).values());
        const filtered = uniq.filter(i => i.symbol.includes(t));

        // apply NASDAQ filter when ready
        const finalList = nasdaqSymbols.length
          ? filtered.filter(i => nasdaqSymbols.includes(i.symbol))
          : filtered;

        setSearchResults(finalList);
      } catch (err) {
        console.error('Finnhub API Hatası:', err);
        setSearchResults([]);
      }
    };

    const timeout = setTimeout(doMultiSearch, 500);
    return () => clearTimeout(timeout);
  }, [searchTerm, nasdaqSymbols]);

  return (
    <div className="landing-container">
      {/* ... header & hero omitted for brevity ... */}
      <aside className="markets-sidebar">
        <h3>Piyasalar (Finnhub)</h3>
        <div className="search-bar">
          <input
            type="text"
            placeholder="Hisse veya Kripto Ara..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
        </div>
        <ul className="market-list">
          {searchResults.length === 0 && searchTerm.trim().length >= 2 ? (
            <li className="market-item">Sonuç bulunamadı.</li>
          ) : (
            searchResults.map((item, i) => (
              <li key={i} className="market-item">
                <Link to={`/market/${item.symbol}`}>
                  <span className="market-name">
                    {item.symbol} – {item.description}
                  </span>
                </Link>
              </li>
            ))
          )}
        </ul>
      </aside>
      {/* ... rest of page ... */}
    </div>
  );
}

export default LandingPage;
